import React, { useCallback, useEffect, useState } from 'react'

import UnitsList from './Units/UnitsList';

const Units = () => {

  const [units, setUnits] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUnitsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://swapi.dev/api/films');

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const transformedUnits = data.results.map(
        movieData => {
          return {
            id: movieData.episode_id,
            title: movieData.title,
            openingText: movieData.opening_crawl,
            releaseDate: movieData.release_date
          }
        });
      setUnits(transformedUnits);
    }
    catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchUnitsHandler();
  }, [fetchUnitsHandler]);

  return (
    <div>
      <section>
        <button onClick={fetchUnitsHandler}>Fetch Units</button></section>
      <section>
        {!isLoading && units.length > 0 && <UnitsList units={units}/>}
        {!isLoading && units.length === 0 && !error && <p>Found no units.</p>}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </div>
  )
};

export default Units;
