import React, { useCallback, useEffect, useState } from 'react'

const Units = (props) => {

  const [units, setUnits] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUnitsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("./src/aoeUnits.json");

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await JSON.parse(response);

      const transformedUnits = data.results.map(
        aoeUnits => {
          return {
            id: aoeUnits.id,
            name: aoeUnits.name,
            description: aoeUnits.description,
            expansion: aoeUnits.expansion,
            age: aoeUnits.age,
            cost: aoeUnits.cost,
            build_time: aoeUnits.build_time,
            reload_time: aoeUnits.reload_time,
            attack_delay: aoeUnits.attack_delay,
            movement_rate: aoeUnits.movement_rate,
            line_of_sight: aoeUnits.line_of_sight,
            hit_points: aoeUnits.hit_points,
            range: aoeUnits.range,
            attack: aoeUnits.attack,
            armor: aoeUnits.armor,
            accuracy: aoeUnits.accuracy
          }
    });
    setUnits(transformedUnits);
  }
  catch (error) {
    setError(error.message);
  }
  setIsLoading(false);
}, []);

useEffect(() => {
  fetchUnitsHandler();
}, [fetchUnitsHandler]);

  return (
    <div>
      <section><button onClick={fetchUnitsHandler}>Fetch Units</button></section>
<section>
  {!isLoading && units.length >0 && <div>{units}</div>}
  {!isLoading && units.length === 0 && !error && <p>Found no units.</p>}
  {!isLoading && error && <p>{error}</p>}
  {isLoading && <p>Loading...</p>}
</section>
    </div>
  )
};

export default Units;
