import React, { useCallback, useEffect, useState } from 'react'

const Units = (props) => {

  const [units, setUnits] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUnitsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("./src/age-of-empires-units.json");

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const transformedUnits = data.results.map(
        unitsdata => {
          return {
            id: unitsdata.id,
            name: unitsdata.name,
            description: unitsdata.description,
            expansion: unitsdata.expansion,
            age: unitsdata.age,
            cost: unitsdata.cost,
            build_time: unitsdata.build_time,
            reload_time: unitsdata.reload_time,
            attack_delay: unitsdata.attack_delay,
            movement_rate: unitsdata.movement_rate,
            line_of_sight: unitsdata.line_of_sight,
            hit_points: unitsdata.hit_points,
            range: unitsdata.range,
            attack: unitsdata.attack,
            armor: unitsdata.armor,
            accuracy: unitsdata.accuracy
          }
    });
    setUnits(transformedUnits);
  }
  catch (error) {
    setError(error.message);
  }
  setIsLoading(false);
}, []);

useEffect(() => {
  fetchUnitsHandler();
}, [fetchUnitsHandler]);

  return (
    <div>
<button onClick={fetchUnitsHandler}>Fetch Units</button>
    </div>
  )
};

export default Units;
