import React, { useState } from 'react';
import './App.css';
import Units from './components/Units';

const App = () => {

const [currentView, setCurrentView] = useState("HomePage");

const HomePageHandler = () => {
setCurrentView("Homepage");
}

const UnitsPageHandler = () => {
setCurrentView("UnitsPage");
}

if (currentView === "Homepage") {
  return (
    <div>
      <div className='header'>
      <h1 className='h1'>Home Page</h1>
      <nav className='navbar'>
        <button className='btn btn-1' onClick={HomePageHandler}>Home</button>
        <button className='btn btn-2' onClick={UnitsPageHandler}>Units</button>
      </nav>
      </div>
    </div>
  )
}

if (currentView === "UnitsPage") {
  return (
    <div>
      <div className='header'>
      <h1 className='h1'>Home Page</h1>
      <nav className='navbar'>
        <button className='btn btn-1' onClick={HomePageHandler}>Home</button>
        <button className='btn btn-2' onClick={UnitsPageHandler}>Units</button>
      </nav>
      </div>
    </div>
  )
}

};

export default App;
