import React, { useState} from 'react';

import UnitsList from './Units/UnitsList';

const Units = () => {

  const [units, setUnits] = useState([]);
  // const [isLoading, setIsLoading] = useState(false);
  // const [error, setError] = useState(null);

  // const fetchUnitsHandler = useCallback(async () => {
    // setIsLoading(true);
    // setError(null);
      const response = fetch('https://swapi.dev/api/films');

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = response.json();

      const transformedUnits = data.results.map(
        movieData => {
          return {
            id: movieData.episode_id,
            title: movieData.title,
            openingText: movieData.opening_crawl,
            releaseDate: movieData.release_date
          }
        });
      setUnits(transformedUnits);


    // setIsLoading(false);

  // useEffect(() => {
  //   fetchUnitsHandler();
  // }, [fetchUnitsHandler]);

  return (
    <div>
      <section>
        {units.length > 0 && <UnitsList units={units}/>}
        {units.length === 0 && <p>Found no units.</p>}
        {/* { <p>{error}</p>}
        {<p>Loading...</p>} */}
      </section>
    </div>
  )
};

export default Units;
