import React, { useCallback, useEffect, useState } from 'react'

import UnitsList from './Units/UnitsList';

const Units = () => {

  const [units, setUnits] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUnitsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("./src/aoeUnits.json");

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const transformedUnits = data.results.map(
        aoeunit => {
          return {
            id: aoeunit.id,
            name: aoeunit.name,
            description: aoeunit.description,
            expansion: aoeunit.expansion,
            age: aoeunit.age,
            cost: aoeunit.cost,
            build_time: aoeunit.build_time,
            reload_time: aoeunit.reload_time,
            attack_delay: aoeunit.attack_delay,
            movement_rate: aoeunit.movement_rate,
            line_of_sight: aoeunit.line_of_sight,
            hit_points: aoeunit.hit_points,
            range: aoeunit.range,
            attack: aoeunit.attack,
            armor: aoeunit.armor,
            accuracy: aoeunit.accuracy
          }
    });
    setUnits(transformedUnits);
  }
  catch (error) {
    setError(error.message);
  }
  setIsLoading(false);
}, []);

useEffect(() => {
  fetchUnitsHandler();
}, [fetchUnitsHandler]);

  return (
    <div>
      <section>
        <button onClick={fetchUnitsHandler}>Fetch Units</button></section>
<section>
  {!isLoading && units.length >0 && <UnitsList>{units}</UnitsList>}
  {!isLoading && units.length === 0 && !error && <p>Found no units.</p>}
  {!isLoading && error && <p>{error}</p>}
  {isLoading && <p>Loading...</p>}
</section>
    </div>
  )
};

export default Units;
